   constructor(props) {
      super(props);

      this.state = {
         aToken: 'Initial data...',
         aName: 'Initial name...'
      }
      this.updateState = this.updateState.bind(this);
   };
   updateToken(e) {
      this.setState({atoken: e.target.value});
   }
   updateName(e) {
      this.setState({aName: e.target.value});
   }
   render() {
      return (
         <div>
          <label>
            Token: 
            <input type = "text" value = {this.state.aToken} 
