import React from 'react';

class ReferenceRequest extends React.Component {
   constructor(props) {
      super(props);

      this.state = {
         data: 'Initial data...'
      }
      this.updateState = this.updateState.bind(this);
   };
   updateState(e) {
      this.setState({atoken: e.target.value});
   }
   render() {
      return (
         <div>
          <label>
            Token: 
            <input type = "text" value = {this.state.atoken} 
                onChange = {this.updateState} />
          </label>
          <input type="submit" value="Submit" />
          <h4>{this.state.atoken}</h4>
          <label>
            Customer Number: 
            <input type = "text" value = ""
                onChange = ""/>
          </label>
          <input type="submit" value="Submit" />
         </div>
      );
   }
}
export default ReferenceRequest;
